mapper.select:
select action_id, action_type, crawler_date, content from crawler_content
where action_id = #{actionId}
and crawler_date = #{crawlerDate}

select action_id, action_type, crawler_date, content from crawler_content
where action_type = #{actionType}
and crawler_date = #{crawlerDate}

select count(*) from crawler_content
where action_type = #{actionType}
and crawler_date = #{crawlerDate}

select action_id, action_type, crawler_date, content from crawler_content
where action_type = #{actionType}
and crawler_date = #{crawlerDate}
order by action_id
limit #{pageBegin},#{pageCount}
